pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../reference/Sig.sol";

// 功能设置：
// 一、身份认证
// 1. admin给获得空投领取权的用户账户签名
// 2. 用户用签名领取空投
//
// 二、账本管理
// 1. 接收一笔转账包含当期空投所需的所有代币
// 2. 
contract DeepGoAirdrop is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    address public admin;
    address tokenAddress;
    uint256 perAirdrop;
    uint256 totalAirdrop;
    bool airdropIsActive = true;

    mapping(address => bool) public expired;

    event TransferAdmin(address indexed from, address indexed to);

    modifier onlyAdmin() {
        require(address(msg.sender) == admin, "Not Permit");
        _;
    }

    constructor(
        address _tokenAddress,
        uint256 _perAirdrop,
        uint256 _totalAirdrop
    ) {
        admin = msg.sender;
        tokenAddress = _tokenAddress;
        perAirdrop = _perAirdrop;
        totalAirdrop = _totalAirdrop;
    }

    function verified(
        bytes32 hash, 
        bytes memory signature
    ) private view returns (bool)
    {
        return admin == Sig.recover(hash, signature);
    }

    function ethSignedHash(
        address _addr
    ) private pure returns(bytes32) 
    {
        return Sig.ethSignedHash(_addr);
    }

    /*
    * Pause sale if active, make active if paused
    */
    function flipActiveState() public onlyAdmin {
        airdropIsActive = !airdropIsActive;
    }

    /*     
    * Transfer admin
    */
    function transferAdmin(
        address _to
    ) onlyOwner external 
    {
        require(_to != admin, "Transfer Meaningless");
        address _from = admin;
        admin = _to;

        emit TransferAdmin(_from, _to);
    }

    function claimAirdrop(
        bytes memory signature
    ) public nonReentrant 
    {
        require(verified(Sig.ethSignedHash(msg.sender), signature), "Not verified");
        require(!expired[msg.sender], "Expired");
        require(airdropIsActive, "Unactive to mint Animal");     
        require(IERC20(tokenAddress).balanceOf(address(this)) >= perAirdrop, 
                "Unsufficient token");
        IERC20(tokenAddress).safeTransfer(msg.sender, perAirdrop);  
        expired[msg.sender] = true;
    }

    function balance(address _tokenAddress) public view returns (uint256) {
        return IERC20(_tokenAddress).balanceOf(address(this));
    }

    function withdraw(
        address _tokenAddress,
        uint256 _amount
    ) onlyOwner external 
    {
        require(IERC20(_tokenAddress).balanceOf(address(this)) >= _amount, 
                "Unsufficient token");
        IERC20(tokenAddress).safeTransfer(msg.sender, _amount); 
    }

}
